import React, { useState, useRef,useEffect  } from 'react';
import axios from 'axios';
import './CounterComponent.css';
import defaultImage from './logo.svg';
import { Modal } from '@mui/material';
import  Results  from './Results';

const CounterComponent = () => {
    const [isCameraOpen, setIsCameraOpen] = useState(false);
    const [processedImageSrc, setProcessedImageSrc] = useState('');
    const [output, setOutput] = useState([]);
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const processedImageRef = useRef(null);
    const captureBtnRef = useRef(null);
    const sum=document.getElementById('sum');
    const [isModal,setModel]=useState(false);

    useEffect(() => {
      // Set the default image source on component mount
      setProcessedImageSrc(defaultImage);
    }, []);
    useEffect(() => {
      // Hide the 'sum' div when the modal is closed and output is empty
      if (!isModal && output.length === 0) {
        sumRef.current.style.display = 'none';
      }
    }, [isModal, output]);

    const updateOutput = (data) => {
      var Output_box=document.getElementById('sum');
      Output_box.style.display="flex";
           var sum = document.getElementsByClassName('output');
          var output=0;
          var javascript_variable=data;
          for (var i = 0; i < javascript_variable.length; i++) {
      output += javascript_variable[i] === 2? 40 :
                javascript_variable[i] === 1 ? 10 :
                javascript_variable[i] === 0 ? 30 :
                0;}
                sum.textContent = "   "+output;
            setOutput(output);
    };
    const openCamera = () => {
      navigator.mediaDevices.getUserMedia({ video: true })
        .then((stream) => {
            processedImageRef.current.style.display = 'none';
            videoRef.current.style.display = 'block';
          videoRef.current.srcObject = stream;
          videoRef.current.play();
          setIsCameraOpen(true);
          captureBtnRef.current.textContent = 'Capture';
        })
        .catch((error) => {
          console.error('Error accessing the camera: ', error);
        });
    };
  
    const captureImage = () => {
        const video = videoRef.current;
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
      
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
      
        video.pause();
        ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
      
        const dataURL = canvas.toDataURL('image/jpeg');
      
        const stream = video.srcObject;
        const tracks = stream.getTracks();
        tracks.forEach((track) => track.stop());
      
        axios
          .post('http://127.0.0.1:5000/process-image', { image: dataURL })
          .then((response) => {
            const timestamp = Date.now();
            const processedImageUrl = `http://127.0.0.1:5000/processed-image/processed_image.jpg?t=${timestamp}`;
            setProcessedImageSrc(processedImageUrl);
            processedImageRef.current.style.display = 'block';
            videoRef.current.style.display = 'none';

            captureBtnRef.current.textContent = 'Capture Image';
            captureBtnRef.current.disabled = false;
            setModel(true);
            updateOutput(response.data.output);
            setIsCameraOpen(false);
          })
          .catch((error) => {
            console.error('Error processing the image:', error);
          });
      };
  
    const toggleCapture = () => {
      if (!isCameraOpen) {
        setIsCameraOpen(true);
        captureBtnRef.current.textContent = 'Capture';
        openCamera();
      } else {
        captureBtnRef.current.disabled = true;
        captureBtnRef.current.textContent = 'Processing...';
        captureImage();
      }
    };
    const handleCloseModal = () => {
      setModel(false);
      sum.style.display = 'none';
    };
  
    return (
      <div>
        <div className="wave"></div>
        <div className="wave"></div>
        <div className="wave"></div>
        <div className="container">
          <h1>Cashierless Counter</h1>
        </div>
        <div className="center">
          <div id="video-container">
            <video id="video" width="640" height="480" style={{ height: '450px', width: '600px', borderRadius: '10%', boxShadow: '0 0 10px rgba(0.3, 0.3, 0.3, 0.7)', display: 'none' }} ref={videoRef}></video>
            <img id="processed-image" src={processedImageSrc} style={{ height: '450px', width: '600px', borderRadius: '10%', boxShadow: '0 0 10px rgba(0.3, 0.3, 0.3, 0.7)' }}ref={processedImageRef}></img>
          </div>
          <canvas id="canvas" style={{ display: 'none' }} ref={canvasRef}></canvas>
        </div>
        <div className="center">
          <button id="capture-btn" onClick={toggleCapture} ref={captureBtnRef}>Capture Image</button>
        </div>
        <div className='modaldiv'>

        <Modal 
        open={isModal}
        onClose={handleCloseModal}
        className='modal'
       >
       <Results path={processedImageSrc} output={output} closeModel={setModel} processimage={setProcessedImageSrc} outoutState={setOutput} />
       
       </Modal>
        </div>
       
      </div>
    );
  };
  
  export default CounterComponent;
