import React from 'react';
import defaultImage from './logo.svg';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import './modal.css';
import defaultqr from './download.png';


const Results = (props) => {
  const {path,output,closeModel,processimage}=props
  console.log(output)
  function createData(Item, Quantity, Cost) {
    return {Item, Quantity, Cost};
  }
  let map=[];
  let names=["apple","banana","orange"];
  let counter=[0,0,0]
  let cost=[30,10,50]
  let totalcost=0;
  output.forEach((item)=>{
    if(item ===2 )
    {
      counter[2]++;
  }
    else if(item ===1 )
    {  
      counter[1]++;
    
  }
  else
  {
    counter[0]++; 
}
    })
    let i = 0;

while (i < counter.length) {
    if(counter[i]!==0){
      map=[createData(names[i],counter[i],cost[i]*counter[i])]
      totalcost=cost[i]*counter[i];
    }
    i++;
}

  function onclickfun(){
    
    processimage(defaultImage)
    closeModel(false);
  }
  return (
    <div className='total'>
    <div className='leftside'>
      <img src={path} id="imagefinal"></img>
      <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Item</TableCell>
            <TableCell align="right">Quantity&nbsp;</TableCell>
            <TableCell align="right">Cost&nbsp;(Rs)</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {map.map((row) => (
            <TableRow
              key={row.name}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.Item}
              </TableCell>
              <TableCell align="right">{row.Quantity}</TableCell>
              <TableCell align="right">{row.Cost}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
      <button onClick={onclickfun}></button>
    </div>
    <div className='rightside'>
      <h2> The toatal payable amount is Rs. {totalcost}</h2>
      <button className='paymentbtn' onClick=""> Pay With Solana</button>
      <button className='paymentbtn upi' onClick=""> Pay With Upi</button>
      <div id="qrimage"></div><img src={defaultqr}></img>
    </div>
    </div>
  );
};

export default Results;
