import { Cluster, clusterApiUrl, Connection, PublicKey, Keypair } from "@solana/web3.js";
import { encodeURL, createQR } from "@solana/pay";
import BigNumber from "bignumber.js";
import { useRef } from "react";


export default function Home() {

    const ref = useRef(null);

    function createCode(recipientAddr, Amount) {
      const recipient = new PublicKey(recipientAddr);
      const amount = new BigNumber(Amount);
      const reference = new Keypair().publicKey;
      const label = "";
      const message = "";
      const memo = "";
      const url = encodeURL({ recipient, amount, reference, label, message, memo });
       
      if (typeof window !== "undefined") {
        
        const qrCode = createQR(url);
        qrCode.append(ref.current);
      }
    }

    createCode('kTTpYq1DrudiVtnGKVwZcc5SYvRi3PhF2ai5kVqzkiu',0.1)

    return (
      <div>
        <button onClick={createCode()}/>
      </div>
    )
}
