import { PublicKey } from '@solana/web3.js';
import { createTxQr } from '@solana/pay';

// Function to create a QR code
function generateQR(recipientAddress, amount) {
  try {
    // Convert recipient address to PublicKey
    const recipientPublicKey = new PublicKey(recipientAddress);

    // Create the transaction QR code
    const qrCode = createTxQr(recipientPublicKey, amount);

    // Print or display the QR code
    console.log(qrCode); // Replace with your preferred method of displaying the QR code
  } catch (error) {
    console.error('Error generating QR code:', error);
  }
}

// Example usage
const recipientAddress = 'your_recipient_address';
const amount = 100; // Amount in Solana tokens

generateQR(recipientAddress, amount);
