import React from 'react';
import QRCode from 'qrcode.react';

const UPIQRCode = ({ upiId, amount }) => {
  const upiUrl = `upi://pay?pa=${encodeURIComponent(upiId)}&am=${encodeURIComponent(amount)}`;

  return (
    <div>
      <QRCode value={upiUrl} />
    </div>
  );
};

export default UPIQRCode;
