import React from 'react';
import QRCode from 'qrcode.react';
import { saveSvg } from 'svg-capture';
import './modal.css';

const UPIQRCode = ({ amount }) => {
  const upi = 'shlok08@ybl';
  const name = 'Shlok';
  const upiUrl = `upi://pay?pa=${upi}&pn=${name}&am=${amount}&cu=INR`;

  const handleSaveQRCode = () => {
    const qrElement = document.getElementById('qr');
    console
    
    // Capture the SVG and save it as a file
    saveSvg(qrElement, 'qrcode.svg');
  };

  return (
    <div>
      <QRCode value={upiUrl} id='qr' />

      <button onClick={handleSaveQRCode}>Save QR Code</button>
    </div>
  );
};

export default UPIQRCode;
