import {
    Cluster,
    clusterApiUrl,
    Connection,
    PublicKey,
    Keypair,
    LAMPORTS_PER_SOL,
} from "@solana/web3.js";
import { encodeURL, createQR } from "@solana/pay";
import BigNumber from "bignumber.js";
import { useEffect, useRef, useState } from "react";

const Home = (props) => {
    const {repaddress,amount}=props
    const [balance, setBalance] = useState(0);
    const [txDone, setTxDone] = useState(false);

    useEffect(() => {
        passProps();
        const timer = setInterval(() => {
            checkTx();
        }, 1000);

        return () => {
            clearInterval(timer);
        };
    }, []);

    async function passProps() {
        await createTxQr(repaddress, amount);
    }

    const ref = useRef(null);

    async function checkTx() {
        const balNew = await fetchBalance(repaddress);
        console.log("checked", balNew);
        console.log("balance dekhlo", balance)
        if (balNew != balance) {
            setTxDone(true);
            console.log("tx hogaya bro", balance);
        }
    }

    async function fetchBalance( repaddress) {
        const address = repaddress;
        const key = new PublicKey(address);
        const connection = new Connection(clusterApiUrl("devnet"));
        const bal = await connection.getBalance(key);
        return bal / LAMPORTS_PER_SOL;
    }

    async function createTxQr(recipientAddr, Amount) {
        const recipient = new PublicKey(recipientAddr);
        const amount = new BigNumber(Amount);
        const reference = new Keypair().publicKey;
        const label = ""; //project name
        const message = ""; //what message on tx ?
        const memo = ""; //project name
        const url = encodeURL({
            recipient,
            amount,
            reference,
            label,
            message,
            memo,
        });
        console.log("url", url.href);

        if (typeof window !== "undefined") {
            const qrCode = createQR(url);
            qrCode.append(ref.current);
        }

        await fetchBalance()
        .then((e) => {setBalance(e)})
        
        console.log("bro bal", balance)
    }

    return (
        <div>
            <div ref={ref} />
        </div>
  );
}

export default Home;
