const UPIQRCode = ({ amount }) => {
  const upi = 'shlok08@ybl';
  const name = 'Shlok';
  const upiUrl = `upi://pay?pa=${upi}&pn=${name}&am=${amount}&cu=INR`;

  const handleSaveQRCode = () => {
    const svgElement = document.getElementById('qr').getElementsByTagName('svg')[0];
    const svgString = new XMLSerializer().serializeToString(svgElement);

    const blob = new Blob([svgString], { type: 'image/svg+xml' });
    saveAs(blob, 'qrcode.svg');
  };

  return (
    <div>
      <QRCode value={upiUrl} id='qr' />

      <button onClick={handleSaveQRCode}>Save QR Code</button>
    </div>
  );
};

export default UPIQRCode;
