import React, { useState, useRef } from 'react';
import './CounterComponent.css';
const CounterComponent = () => {
    const [isCameraOpen, setIsCameraOpen] = useState(false);
    const [processedImageSrc, setProcessedImageSrc] = useState('');
    const [output, setOutput] = useState('');
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const captureBtnRef = useRef(null);
    const videoid=document.getElementById('video');
    const processedImage = document.getElementById('processed-image');

  
    const openCamera = () => {
      navigator.mediaDevices.getUserMedia({ video: true })
        .then((stream) => {
        
        videoid.style.display='block';
          videoRef.current.srcObject = stream;
          videoRef.current.play();
          setIsCameraOpen(true);
          captureBtnRef.current.textContent = 'Capture';
        })
        .catch((error) => {
          console.error('Error accessing the camera: ', error);
        });
    };
  
    const captureImage = () => {
      const video = videoRef.current;
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
  
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
  
      video.pause();
      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
  
      const dataURL = canvas.toDataURL('image/jpeg');
  
      const stream = video.srcObject;
      const tracks = stream.getTracks();
      tracks.forEach((track) => track.stop());
  
      fetch('/process-image', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ image: dataURL }),
      })
        .then((response) => response.json())
        .then((result) => {
          const timestamp = Date.now();
          setProcessedImageSrc(result.processed_image + '?t=' + timestamp);
          setOutput(result.output);
          setIsCameraOpen(false);
        })
        .catch((error) => {
          console.error('Error processing the image:', error);
        });
    };
  
    const toggleCapture = () => {
      if (!isCameraOpen) {
        setIsCameraOpen(true);
        captureBtnRef.current.textContent = 'Capture';
        openCamera();
      } else {
        captureBtnRef.current.disabled = true;
        captureBtnRef.current.textContent = 'Processing...';
        captureImage();
      }
    };
  
    return (
      <div>
        <div className="wave"></div>
        <div className="wave"></div>
        <div className="wave"></div>
        <div className="container">
          <h1>Cashierless Counter</h1>
        </div>
        <div className="center">
          <div id="video-container">
            <video id="video" width="640" height="480" style={{ height: '450px', width: '600px', borderRadius: '10%', boxShadow: '0 0 10px rgba(0.3, 0.3, 0.3, 0.7)', display: 'none' }} ref={videoRef}></video>
            <img id="processed-image" src={processedImageSrc} style={{ height: '450px', width: '600px', borderRadius: '10%', boxShadow: '0 0 10px rgba(0.3, 0.3, 0.3, 0.7)' }}></img>
          </div>
          <canvas id="canvas" style={{ display: 'none' }} ref={canvasRef}></canvas>
        </div>
        <div className="center">
          <button id="capture-btn" onClick={toggleCapture} ref={captureBtnRef}>Capture Image</button>
        </div>
        <div id="sum" style={{ display: output ? 'flex' : 'none' }}>
          <span className="text_file">The total payable amount for your item is <span className="output" style={{ fontWeight: 'bold', fontSize: '34px' }}>{output}</span></span>
        </div>
      </div>
    );
  };
  
  export default CounterComponent;
